export class MaseJSInterpreter{static interpret(t){const e=document.createDocumentFragment();for(const s in t){const n=this.createElement(s,t[s]);this.applyElementOptions(n,t[s]),e.appendChild(n)}document.body.appendChild(e)}static createElement(t,e){if("string"==typeof e){const t=document.createElement("custom-element");return t.innerHTML=e,t}return document.createElement(t)}static applyElementOptions(t,e){if("object"==typeof e)for(const s in e)switch(s){case"value":t.textContent=e[s];break;case"styles":this.applyStyles(t,e[s]);break;case"class":t.className=e[s];break;case"id":t.id=e[s];break;case"events":this.applyEventListeners(t,e[s]);break;default:if(Array.isArray(e[s]))for(const n of e[s]){const e=this.createElement(s,n);this.applyElementOptions(e,n),t.appendChild(e)}else t.setAttribute(s,e[s])}}static applyStyles(t,e){if(e){const s=e.split(";");for(const e of s)if(""!==e.trim()){const[s,n]=e.split(":");t.style[s.trim()]=n.trim()}}}static applyEventListeners(t,e){if(e)for(const s in e)t.addEventListener(s,e[s])}}