export class MaseJSInterpreter{static interpret(t){for(const e in t){const s=this.createElement(e,t[e]);this.applyElementOptions(s,t[e]),document.body.appendChild(s)}}static createElement(t,e){if("string"==typeof e){const t=document.createElement("custom-element");return t.innerHTML=e,t}return document.createElement(t)}static applyElementOptions(t,e){if("object"==typeof e)for(const s in e)if("value"===s)t.textContent=e[s];else if("styles"===s)this.applyStyles(t,e[s]);else if("class"===s){e[s].split(" ").forEach((e=>t.classList.add(e)))}else if("id"===s){e[s].split(" ").forEach((e=>t.id+=`${e}`))}else if(Array.isArray(e[s]))for(const i of e[s]){const e=this.createElement(s,i);this.applyElementOptions(e,i),t.appendChild(e)}else t.setAttribute(s,e[s])}static applyStyles(t,e){if(e){const s=e.split(";");for(const e of s)if(""!==e.trim()){const[s,i]=e.split(":");t.style[s.trim()]=i.trim()}}}}